import java.util.Timer;
import java.util.TimerTask;

import game.statistics.Statistics;

class PrintStats extends TimerTask {
    private Statistics statistics;
    private int count = 0;

    public PrintStats(Statistics stats) {
        this.statistics = stats;
    }

    public void run() {
        statistics.printStatistics();
        count++;
        System.out.println("Count: " + count);
    }
}

public class App {

    public static void main(String[] args) throws Exception {
        Statistics statistics = new Statistics();

        System.out.println(" [*] Waiting game to start...");

        // And From your main() method or any othr method
        // Timer timer = new Timer();
        // timer.schedule(new PrintStats(statistics), 0, 1000);

        statistics.setup();
    }
}
